fun getRoot(): Request {
    val url = BuildConfig.BOURSORAMA_ROOT_URL
    Log.d("getRoot", url)
    return Request.Builder()
            .url(url)
            .get()
            .build()
}

fun getLoginPage(): Request {
    val url = BuildConfig.BOURSORAMA_ROOT_URL+BuildConfig.BOURSORAMA_LOGIN
    Log.d("getLoginRequest", url)
    return Request.Builder()
            .url(url)
            .get()
            .build()
}

fun getVirtualKeyboard(): Request {
    val url = BuildConfig.BOURSORAMA_ROOT_URL+BuildConfig.BOURSORAMA_VIRTUAL_KEYBOARD
    Log.d("getVirtualKeyboard", url)
    return Request.Builder()
            .url(url)
            .get()
            .build()
}

fun getMovementsRequest(account: Account) : Request {
    val url = BuildConfig.BOURSORAMA_ROOT_URL+account.href+"mouvements"
    Log.d("getMovementsRequest", url)
    return Request.Builder()
            .url(url)
            .get()
            .build()
}

