    return Request.Builder()
            .url(url)
            .get()
            .build()
}

fun getBudget(): Request {
    val url = BuildConfig.BOURSORAMA_ROOT_URL+BuildConfig.BOURSORAMA_BUDGET
    Log.d("getBudget", url)
    return Request.Builder()
            .url(url)
            .get()
            .build()
}